import React, { useState } from 'react';
import { addSpend } from '../services/api';

const AddSpend = () => {
  const [formData, setFormData] = useState({
    db_name: 'spend',
    table_name: 'spends',
    category: '',
    amount: '',
    currency: '',
    date: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addSpend(formData)
      .then(response => {
        console.log(response.data);
        // Optionally, fetch the updated list of spends
        fetchSpends()
          .then(response => {
            setSpends(response.data);
          })
          .catch(error => {
            console.error(error);
          });
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="category" value={formData.category} onChange={handleChange} placeholder="Category" required />
      <input type="number" name="amount" value={formData.amount} onChange={handleChange} placeholder="Amount" required />
      <input type="text" name="currency" value={formData.currency} onChange={handleChange} placeholder="Currency" required />
      <input type="date" name="date" value={formData.date} onChange={handleChange} placeholder="Date" required />
      <button type="submit">Add Spend</button>
    </form>
  );
};

export default AddSpend;
